import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  name: 'InvestStocks',
  data() {
    return {
      products: [],
      selectedProducts: []
    };
  },
  mounted() {
    axios.get('http://localhost:8081/api/v1/products').then(response => {
      this.products = response.data.content;
    }).catch(error => {
      console.log(error);
    });
  },
  computed: {},
  methods: {
    isAddedProduct(product) {
      if (this.selectedProducts.length > 0) {
        let selectedProduct = this.selectedProducts.find(p => p.productId === product.productId);
        return !!selectedProduct;
      }
      return false;
    },
    addProduct(product) {
      this.selectedProducts.push({
        userId: 1,
        productId: product.productId,
        productCode: product.productCode,
        orderAmount: product.unitPrice,
        createdOn: new Date().toLocaleDateString()
      });
    },
    removeProduct(selectProduct) {
      this.selectedProducts = this.selectedProducts.filter(product => product.productId !== selectProduct.productId);
    },
    submitProducts() {
      axios.post('http://localhost:8081/api/v1/products', this.selectedProducts).then(response => {
        console.log(response.data.message);
      }).catch(error => {
        console.log(error);
      });
    }
  }
};
