import axios from "axios";
export default {
  name: 'InvestStocks',
  data() {
    return {
      fields: [{
        key: 'stocks',
        label: 'Stocks',
        sortable: true
      }, {
        key: 'unitePrice',
        label: 'Unit Price (Tk) Gain/Loss (%)',
        sortable: true
      }, {
        key: 'yearToDate',
        label: 'Year to Date',
        sortable: true
      }],
      items: []
    };
  },
  mounted() {
    axios.get('http://localhost:8081/api/v1/products').then(response => {
      this.items = response.data.content;
      this.items.forEach(item => {
        item.stocks = item.productName;
      });
    }).catch(error => {
      console.log(error);
    });
  }
};
